import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import Calendar from 'react-calendar';
import Clock from 'react-clock';

class MainCalendar extends React.Component {
    state = {
      date: new Date(),
    }

    onChange = date => this.setState({ date })

    render() {
      return (
        <div>
          <Calendar
            onChange={this.onChange}
            value={this.state.date}
          />
        </div>
      );
    }
  }

  class MainClock extends React.Component {
    state = {
      date: new Date(),
    }

    componentDidMount() {
      setInterval(
        () => this.setState({ date: new Date() }),
        1000
      );
    }

    render() {
      return (
        <div>
          <Clock
            value={this.state.date}
          />
        </div>

      );
    }
  }
  <Container>
  <Row>
    <Col>1 of 2</Col>
    <Col>2 of 2</Col>
  </Row>
  <Row>
    <Col>1 of 3</Col>
    <Col>2 of 3</Col>
    <Col>3 of 3</Col>
  </Row>
</Container>

ReactDOM.render(<MainClock />, document.getElementById('root'));
